import "../assets/css/galeria.css";
import Heart from "./Heart";
import { useContext, useEffect } from "react";
import { Context } from "../Context";

export default function Galeria() {
  const {fotos, setFotos} = useContext(Context) 
  useEffect(()=>{
    console.log("galeria", fotos);
  }, [])

  const setFavorito = (id) => {
    const fotoIndex = fotos.fotoIndex((foto)=>foto.id===i)
    fotos[fotoIndex].favorito=!fotos[fotoIndex].favorito
    setFotos([...fotos])
  }

  return (
    <div className="galeria grid-columns-5 p-3">
      {
        fotos.map((foto)=> (
          <div onClick={()=>setFavorito(foto.id)}
          className="foto"
          style={{backgroundImage:"url(${foto.src})"}}
          key={foto.id}>
          <Heart filled={foto.favorito}/>
          <p>{foto.desc}</p>  
          </div>
        ))
      }
    </div>
  );
}
